function Animal() {
    this.animals = [];
    this.lines = [];
    this.currentAnimal = 0;

    this.F = 0.1;
    this.W = 5;

    this.canvas = null;
    this.ctx = null;
    this.gradient = null;

    this.k = 1;
    this.mX = 0;
    this.mY = 0;
    this.IMAGE_WIDTH = 2000;
    this.IMAGE_HEIGHT = 2000;
    this.canvasWidth = 2000;
    this.canvasHeight = 2000;


    this.init = function (animals) {
        this.animals = this.parseData(animals);
        this.initCanvas();
        this.resize();
        this.initEvent();
        this.initLoad(0);
        this.render();
    };

    this.initEvent = function () {
        document.addEventListener('click', this.initLoad.bind(this));
        window.addEventListener('resize', this.resize.bind(this));
        document.addEventListener('wheel', this.initLoad.bind(this));
    };

    this.initCanvas = function () {
        this.canvas = document.getElementById("animate");
        this.ctx = this.canvas.getContext("2d");


    };

    this.resize = function () {
        this.canvasWidth = window.innerWidth;
        this.canvasHeight = window.innerHeight;
        this.mX = 0;
        this.mY = 0;

        if (this.canvasWidth > this.canvasHeight) {
            this.k = this.canvasHeight / this.IMAGE_HEIGHT;
            this.mX = (this.canvasWidth - this.IMAGE_WIDTH * this.k) / 2;
        } else {
            this.k = this.canvasWidth / this.IMAGE_WIDTH;
            this.mY = (this.canvasHeight - this.IMAGE_HEIGHT * this.k) / 2;
        }

        this.canvas.width = this.canvasWidth;
        this.canvas.height = this.canvasHeight;
        this.gradient = this.ctx.createLinearGradient(0, this.mY, 0, this.canvasHeight - 2 * this.mY);
        this.gradient.addColorStop(0, "#ff992d");
        this.gradient.addColorStop(this.mY / this.canvasHeight, "#ff992d");
        this.gradient.addColorStop(0.6, "#e03470");
        this.gradient.addColorStop((this.canvasHeight - this.mY) / this.canvasHeight, "#633564");
        this.gradient.addColorStop(1, "#633564");

        this.W = Math.min(5, Math.max(2, this.canvasWidth/400 + 1.25));
    };


    this.parseData = function (animals) {
        let stages = [];
        for (let i = 0; i < animals.length; i++) {
            stages.push(this.parseString(animals[i].data));
        }
        return stages;
    };

    this.parseString = function (lines) {
        return lines.split(";").map(function (line) {
            return line.split("-").map(function (point) {
                return point.split(",").map(function (coor) {
                    return parseInt(coor);
                });
            });
        });
    };

    this.initLoad = function (index) {
        index = Number.isInteger(index)
            ? index :
            (++this.currentAnimal % this.animals.length);
        this.load(this.animals[index]);
    };

    this.load = function (animal) {
        let scene = [];
        for (let i = 0; i < animal.length; i++) {
            scene.push(this.line(this.getCurrent(i), animal[i]));
        }
        this.lines = scene;
    };

    this.getCurrent = function (index) {
        let sX = this.canvasWidth;
        let sY = this.canvasHeight / 2;
        let eX = this.canvasWidth / 2;
        let eY = this.canvasHeight;
        if (this.lines.length) {
            let arr = this.lines[index % this.lines.length].current;
            sX = this.value(arr[0][0], this.lines[index % this.lines.length]);
            sY = this.value(arr[0][1], this.lines[index % this.lines.length]);
            eX = this.value(arr[1][0], this.lines[index % this.lines.length]);
            eY = this.value(arr[1][1], this.lines[index % this.lines.length]);
        }
        return [[sX, sY], [eX, eY]];
    };

    this.value = function (value, a) {
        let pValue = parseInt(value);
        isNaN(pValue) && console.log(a);
        return isNaN(pValue) ? 0 : pValue;
    };

    this.line = function (current, end) {
        return {
            current: current,
            end: end,
        }
    };

    this.update = function () {
        for (let a = 0; a < this.lines.length; a++) {
            this.lines[a].current[0][0] = this.lines[a].current[0][0] + this.F * (this.lines[a].end[0][0] - this.lines[a].current[0][0]);
            this.lines[a].current[0][1] = this.lines[a].current[0][1] + this.F * (this.lines[a].end[0][1] - this.lines[a].current[0][1]);
            this.lines[a].current[1][0] = this.lines[a].current[1][0] + this.F * (this.lines[a].end[1][0] - this.lines[a].current[1][0]);
            this.lines[a].current[1][1] = this.lines[a].current[1][1] + this.F * (this.lines[a].end[1][1] - this.lines[a].current[1][1]);
        }

    };

    this.renderAnimal = function () {
        for (let j = 0; j < this.lines.length; j++) {
            this.ctx.beginPath();
            this.ctx.strokeStyle = this.gradient;
            this.ctx.lineWidth = this.W;
            this.ctx.lineCap = "round";
            this.ctx.moveTo(
                this.mX + this.k * this.lines[j].current[0][0],
                this.mY + this.k * this.lines[j].current[0][1]
            );
            this.ctx.lineTo(
                this.mX + this.k * this.lines[j].current[1][0],
                this.mY + this.k * this.lines[j].current[1][1]
            );
            this.ctx.stroke();
        }
    };

    this.render = function () {
        requestAnimationFrame(this.render.bind(this));
        this.ctx.fillStyle = "#3b3b3b";
        this.ctx.fillRect(0, 0, this.canvasWidth, this.canvasHeight);
        this.update();
        this.renderAnimal();
    };
}

